query:
-- Pegar o primeiro aluno da lista
SET @studentId = (SELECT id FROM students LIMIT 1);

-- Ver quais disciplinas ele está matriculado
SELECT 
  @studentId as student_id,
  se.subjectId,
  sub.name as subject_name
FROM subjectEnrollments se
JOIN subjects sub ON se.subjectId = sub.id
WHERE se.studentId = @studentId;

